import ReactDOM from 'react-dom';
import Build from './Build';
import React, { useState } from 'react';


export default function Inputs () {


  	const [inputs, setInputs] = useState({});
  	const handleChange = e => {
  		setInputs(prevState => ({ ...prevState, [e.target.name]: e.target.value }));
return (
  		<div>
				      URL: {inputs.url}
				      Source: {inputs.source}<br />
				      Medium: {inputs.medium}<br />
				      Campaign: {inputs.campaign}<br />
				      Content: {inputs.content}<br />
				      Term: {inputs.term}
      				</div>
      				)
  	}

	const handleSubmit = e => {
		ReactDOM.render(
	  <Build 
	  	url={inputs.url} 
	  	source={inputs.source} 
	  	medium={inputs.medium} 
	  	campaign={inputs.campaign} 
	  	content={inputs.content} 
	  	term={inputs.term} 
	  	/>,
	  document.getElementById('output')
	  );

	  e.preventDefault();
	}

	const concat = () => {
		return (
		<div>
				      URL: {inputs.url}
				      Source: {inputs.source}<br />
				      Medium: {inputs.medium}<br />
				      Campaign: {inputs.campaign}<br />
				      Content: {inputs.content}<br />
				      Term: {inputs.term}
      				</div>
      				)
	}

	return (
		<div className="Inputs">
				<div className="Parameterinput">
					<form onSubmit={handleSubmit}>
					<label>URL:</label>
					 <input
			            name="url"
			            type="text"
			            value={inputs.url || ''}
			            onChange={handleChange} />
					<label>Source:</label>
					 <input
			            name="source"
			            type="text"
			            value={inputs.source || ''}
			            onChange={handleChange} />
					<label>Medium:</label>
					 <input
			            name="medium"
			            type="text"
			            value={inputs.medium || ''}
			            onChange={handleChange} />
			        <label>Campaign Name:</label>
					 <input
			            name="campaign"
			            type="text"
			            value={inputs.campaign || ''}
			            onChange={handleChange} />
			        <label>Content:</label>
					 <input
			            name="content"
			            type="text"
			            value={inputs.content || ''}
			            onChange={handleChange} />
			        <label>Term:</label>
					 <input
			            name="term"
			            type="text"
			            value={inputs.term || ''}
			            onChange={handleChange} />
			         <button>Build QueryString</button>
			        </form>
			        {concat}
				</div>


		</div>
		)
}
