import ReactDOM from 'react-dom';
import Build from './Build';
import React, { useState } from 'react';


export default function Inputs () {

const App = () => {

  	const [utm, setUtm] = useState({
  		url: '',
  		source: '',
  		medium: '',
  		campaign: '',
  		content: '',
  		term:''
  	});

  	const handleChange = ({ target }) => {
    	let value = event.target.value;
    	let name = target.name;

      	setUtm((prevalue) => {
      		return {
      			...prevalue,
      			[name]: value
      		}
      	});
	}

	const handleSubmit = (event) => {
		ReactDOM.render(
	  <Build url={this.utm.url} source={this.utm.source} medium={this.utm.medium} campaign={this.utm.campaign} content={this.utm.content} term={this.utm.term} />,
	  document.getElementById('output')
	  );

	  event.preventDefault();
	}


	return (
		<div className="Inputs">
				<div className="Parameterinput">
					<form onSubmit={handleSubmit}>
					<label>URL:</label>
					 <input
			            name="url"
			            type="text"

			            onChange={handleChange} />
					<label>Source:</label>
					 <input
			            name="source"
			            type="text"

			            onChange={handleChange} />
					<label>Medium:</label>
					 <input
			            name="medium"
			            type="text"

			            onChange={handleChange} />
			        <label>Campaign Name:</label>
					 <input
			            name="campaign"
			            type="text"

			            onChange={handleChange} />
			        <label>Content:</label>
					 <input
			            name="content"
			            type="text"

			            onChange={handleChange} />
			        <label>Term:</label>
					 <input
			            name="term"
			            type="text"

			            onChange={handleChange} />
			         <button>Build QueryString</button>
			        </form>
				</div>


		</div>
		)
	}
}
