import ReactDOM from 'react-dom';
import Build from './Build';
import React, { useState } from 'react';


export default function Inputs () {

const App = () => {

  	const [inputs, setInputs] = useState({});
  	const handleChange = e => setInputs(prevState => ({ ...prevState, [e.target.name]: e.target.value }));




	return (
		<div className="Inputs">
				<div className="Parameterinput">
					<form>
					<label>URL:</label>
					 <input
			            name="url"
			            type="text"
			            value={inputs.url || ''}
			            onChange={handleChange} />
					<label>Source:</label>
					 <input
			            name="source"
			            type="text"
			            value={inputs.source || ''}
			            onChange={handleChange} />
					<label>Medium:</label>
					 <input
			            name="medium"
			            type="text"
			            value={inputs.medium || ''}
			            onChange={handleChange} />
			        <label>Campaign Name:</label>
					 <input
			            name="campaign"
			            type="text"
			            value={inputs.campaign || ''}
			            onChange={handleChange} />
			        <label>Content:</label>
					 <input
			            name="content"
			            type="text"
			            value={inputs.content || ''}
			            onChange={handleChange} />
			        <label>Term:</label>
					 <input
			            name="term"
			            type="text"
			            value={inputs.term || ''}
			            onChange={handleChange} />
			         <button>Build QueryString</button>
			        </form>
				</div>


		</div>
		)
	}
}
